import React, { useEffect, Fragment } from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'
import { getCurrentProfile } from '../../actions/profile'
import Spinner from '../layout/Spinner'
import { Link } from 'react-router-dom'
import DashboardActions from './DashboardActions'

const Dashboard = ({
   getCurrentProfile,
   auth: { user },
   profile: { profile, loading } 
  }) => {

  useEffect(()=>{
    getCurrentProfile();
  }, [])

  return loading && profile == null ? <Spinner/> : <Fragment>
    <div className="container">
    <h1 className="large text-primary">
      Dashboard
    </h1>
    <p className="lead"><i className="fas fa-user" aria-hidden="true">Welcome {user && user.name} </i></p>    
    </div>
    {profile !== null ? 
    <Fragment>
      <DashboardActions/>
    </Fragment> : 
    <Fragment>
    <p>You have not yet set up a profile - Please add some info</p>
    <Link to="/create-profile" className='btn btn-primay my-1'>Create Profile</Link>
    <Link to="/edit-profile" className='btn btn-primay my-1'>Edit Profile</Link>
    </Fragment>}
    </Fragment>
}

Dashboard.propTypes = {
  getCurrentProfile: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired
}

const mappedStateToProps = state => ({
  auth: state.auth,
  profile: state.profile
})

export default connect(mappedStateToProps, { getCurrentProfile }) (Dashboard);
