import axios from "axios";
import { response } from "express";
import { setAlert } from './alert'

import {
    GET_PROFILE,
    PROFILE_ERROR,
    UPDATE_PROFILE
} from './types'

//Get current users profiles

export const getCurrentProfile = () => async dispatch => {
   try {
    const res = await axios.get('http://localhost:5000/api/profile/me');
    dispatch({
        type: GET_PROFILE,
        payload: res.data
    })
    } catch (error) {
    
        dispatch({
            type: PROFILE_ERROR,
            paylod: { msg: error.response.data.msg, status: error.response.status }
        });
   } 
}

//Create or update a profile

export const createProfile = (formData, history, edit = false) => async dispatch => {

    try {
        const config = {
            headers: {
                'Content-type': 'application/json'
            }
        }

        const res = await axios.post('http://localhost:5000/api/profile', formData, config)
        dispatch({
            type: GET_PROFILE,
            payload: res.data
        })

        dispatch(setAlert(edit ? 'Profile updated' : 'Profile created', 'success'))

        if(!edit){
            history.push('/dashboard')
        }

    } catch (error) {

        const errors = error.response.data.errors;

        if(errors){
            errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }

        dispatch({
            type: PROFILE_ERROR,
            paylod: { msg: error.response.data.msg, status: error.response.status }
        });
    }
}

//Add experience

export const addExperience = (formData, history) => async dispatch => {
    try {
        const config = {
            headers: {
                'Content-type': 'application/json'
            }
        }

        const res = await axios.put('http://localhost:5000/api/profile/experience', formData, config)
        dispatch({
            type: UPDATE_PROFILE,
            payload: res.data
        })

        dispatch(setAlert('Experience added', 'success'))

        
        history.push('/dashboard')
        

    } catch (error) {

        const errors = error.response.data.errors;

        if(errors){
            errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }

        dispatch({
            type: PROFILE_ERROR,
            paylod: { msg: error.response.data.msg, status: error.response.status }
        });
    }
}

//Add education

export const addEducation = (formData, history) => async dispatch => {
    try {
        const config = {
            headers: {
                'Content-type': 'application/json'
            }
        }

        const res = await axios.put('http://localhost:5000/api/profile/education', formData, config)
        dispatch({
            type: UPDATE_PROFILE,
            payload: res.data
        })

        dispatch(setAlert('Education added', 'success'))

        history.push('/dashboard')

    } catch (error) {

        const errors = error.response.data.errors;

        if(errors){
            errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }

        dispatch({
            type: PROFILE_ERROR,
            paylod: { msg: error.response.data.msg, status: error.response.status }
        });
    }
}

//Create a function to delete and experience

// Delete experience
export const deleteExperience = (id) => async (dispatch) => {
    try {
      const res = await api.delete(`/profile/experience/${id}`);
  
      dispatch({
        type: UPDATE_PROFILE,
        payload: res.data
      });
  
      dispatch(setAlert('Experience Removed', 'success'));
    } catch (err) {
      dispatch({
        type: PROFILE_ERROR,
        payload: { msg: err.response.statusText, status: err.response.status }
      });
    }
  };
